mapper.select:
select order_id, order_code, trip_type, trip_pnr, contact_name, contact_address, contact_postcode, contact_mail, contact_phone, order_time, user_id, supplier_id, from_city, to_city, from_date, ret_date, session_id from flight_order where 1=1
and order_id = #{order_id}
and order_code = #{order_code}
